import Vue from 'vue';
import Router from 'vue-router';


//auth routes
import login from '@/components/auth/login.vue';


//home routes
import index from '@/components/main/index.vue';
import home from '@/components/pages/home.vue';
import servicesPage from '@/components/pages/services_create_page.vue';
import projectPage from '@/components/pages/project_create_page.vue';
import partenerPage from '@/components/pages/partener_create_page.vue';
import personalizationPage from '@/components/pages/personalization_page.vue';
import realisationPage from '@/components/pages/realisation_create_page.vue';
import contactPage from '@/components/pages/contact_create_page.vue';
import messagePage from '@/components/pages/message_page.vue';
import catalogPage from '@/components/pages/catalog_create_page';
import videoPage from '@/components/pages/video_create_page';
import avantagesPage from '@/components/pages/avantage_create_page';


Vue.use(Router);


function autorization(to, from, next) {
    let isAuthenticated = false;
    //this is just an example. You will have to find a better or
    // centralised way to handle you localstorage data handling
    if (localStorage.getItem('token')) {
        isAuthenticated = true;
    } else {
        isAuthenticated = false;
    }


    if (isAuthenticated) {
        next(); // allow to enter route
    } else {
        next({ name: 'login' }); // go to '/login';
    }
}

export default new Router({
    routes: [{
            path: "/home",
            name: "home",
            beforeEnter: autorization,
            component: index,
            children: [{
                    path: '',
                    name: 'home',
                    component: home
                },
                {
                    path: '/create/services',
                    component: servicesPage,
                    name: "services"
                },
                {
                    path: '/create/project',
                    component: projectPage,
                    name: "projects"
                },
                {
                    path: '/create/partener',
                    component: partenerPage,
                    name: "parteners",
                },
                {
                    path: '/personalization',
                    component: personalizationPage,
                    name: 'config'
                },
                {
                    path: '/realisation',
                    component: realisationPage,
                    name: 'realisation'
                },

                {
                    path: '/contact',
                    component: contactPage,
                    name: 'contact'
                },


                {
                    path: '/messages',
                    component: messagePage,
                    name: 'messages'
                },

                {
                    path: '/catalog',
                    component: catalogPage,
                    name: 'catalog'
                },

                {
                    path: '/videos/post',
                    component: videoPage,
                    name: 'video'
                },

                {
                    path: '/avantages',
                    component: avantagesPage,
                    name: 'avantages'
                }
            ]
        },
        {
            path: '/',
            component: login
        },
        //redirect to login
        { path: '*', redirect: '/' }
    ]
});
